import base64
import os
from zipfile import ZipFile

# Base64-–∫–æ–¥—ã –ø—Ä–æ—Å—Ç—ã—Ö PNG-–∑–∞–≥–ª—É—à–µ–∫ (1x1 –ø—Ä–æ–∑—Ä–∞—á–Ω—ã–π –ø–∏–∫—Å–µ–ª—å + —Ç–µ–∫—Å—Ç —á–µ—Ä–µ–∑ Pillow –Ω–µ –Ω—É–∂–µ–Ω ‚Äî –ø—Ä–æ—Å—Ç–æ —Ü–≤–µ—Ç–Ω—ã–µ –∫–≤–∞–¥—Ä–∞—Ç—ã)
images = {
    "banner1.jpg": "data:image/jpeg;base64,",
    "banner2.jpg": "data:image/jpeg;base64,",
    "banner3.jpg": "data:image/jpeg;base64,",
    "category1.jpg": "data:image/jpeg;base64,",
    "category2.jpg": "data:image/jpeg;base64,",
    "category3.jpg": "data:image/jpeg;base64,",
    "category4.jpg": "data:image/jpeg;base64,",
}

# –°–æ–∑–¥–∞—ë–º –ø–∞–ø–∫—É static/images, –µ—Å–ª–∏ –µ—ë –Ω–µ—Ç
os.makedirs("static/images", exist_ok=True)

# –°–æ—Ö—Ä–∞–Ω—è–µ–º –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è
for filename, data_url in images.items():
    header, encoded = data_url.split(",", 1)
    image_data = base64.b64decode(encoded)
    with open(f"static/images/{filename}", "wb") as f:
        f.write(image_data)

# –°–æ–∑–¥–∞—ë–º ZIP-–∞—Ä—Ö–∏–≤
with ZipFile("mebel_placeholders.zip", "w") as zipf:
    for root, dirs, files in os.walk("static"):
        for file in files:
            zipf.write(os.path.join(root, file))

print("‚úÖ ZIP-–∞—Ä—Ö–∏–≤ 'mebel_placeholders.zip' —Å–æ–∑–¥–∞–Ω!")
print("üìÅ –í–Ω—É—Ç—Ä–∏: static/images/banner1.jpg ... category4.jpg")